<?php $this->layout('theme', ['head'=> $head]); ?><!--========= SECTION LOAD BOX ========== --><?php $this->start('load'); ?><div class="ajax_load">    <div class="ajax_load_box">        <div class="ajax_load_box_circle"></div>        <p class="ajax_load_box_title">Aguarde, carregando...</p>    </div></div><?php $this->stop(); ?><!--========= SECTION FOR STYLES ========== --><?php $this->start('styles'); ?><?php $this->stop(); ?><!--==================== PRODUTOS ====================--><section class="product section" id="product">    <!--==== FORM SEARCH ========== -->    <header class="section__search__header ">        <span class="section__title"><?= ($title ?? "Produtos"); ?></span>        <h2 class="section__subtitle"><?= ($search ?? $desc ?? "Informativos e conteúdo") ?></h2>        <form class="section__form" name="search" action="<?= url("/produtos/buscar"); ?>" method="post" enctype="multipart/form-data">            <label>                <input type="text" name="s" placeholder="Encontrar produtos:" class="section__input" required/>                <button>                    <i class="ri-search-line"></i>                </button>            </label>        </form>    </header>    <?php if(empty($products) && !empty($search)): ?>        <!--==== return empty result ========== -->        <div class="section__content">            <div class="empty__content">                <p class="empty_content_title">Sua pesquisa não retornou resultados</p>                <p class="empty_content_desc">Você pesquisou por <b><?= $search; ?></b>. Tente outros termos.</p>                <a class="empty_content_btn"                   href="<?= url("/produtos"); ?>" title="produtos">Voltar aos produtos</a>            </div>        </div>    <?php elseif (empty($products)): ?>        <!--==== return empty posts ========== -->        <div class="section__content">            <div class="empty_content">                <h3 class="empty_content_title">Ainda estamos trabalhando aqui!</h3>                <p class="empty_content_desc">Nossos editores estão preparando um conteúdo de primeira para você.</p>            </div>        </div>    <?php else: ?>        <!--==== return search results ========== -->        <div class="products__container container grid">            <?php foreach ($products as $prod): ?>                  <?php $this->insert("product-card", ["prod" => $prod]); ?>            <?php endforeach; ?>            <p><?= $paginator; ?></p>        </div>    <?php endif; ?></section><!--========= SECTION FOR SCRIPTS ========== --><?php $this->start('scripts'); ?><?php $this->stop(); ?>